import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto";

export default function LiveChart() {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");
    chartInstance.current = new Chart(ctx, {
      type: "line",
      data: {
        labels: [],
        datasets: [
          {
            label: "Live Price",
            data: [],
            borderColor: "#00FFB9",
            borderWidth: 2,
            tension: 0.4,
          },
        ],
      },
      options: {
        responsive: true,
        scales: {
          x: { ticks: { color: "#ccc" } },
          y: { ticks: { color: "#ccc" } },
        },
      },
    });

    const socket = new WebSocket("ws://localhost:8000/ws/chart");
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      const time = new Date(data.timestamp).toLocaleTimeString();
      const price = data.price;

      const chart = chartInstance.current;
      chart.data.labels.push(time);
      chart.data.datasets[0].data.push(price);

      if (chart.data.labels.length > 30) {
        chart.data.labels.shift();
        chart.data.datasets[0].data.shift();
      }
      chart.update();
    };

    return () => socket.close();
  }, []);

  return (
    <div style={{ background: "#1f2833", padding: 20, borderRadius: 12 }}>
      <h2 style={{ color: "#fff", textAlign: "center" }}>ðŸ“ˆ Live EMA + Supertrend Chart</h2>
      <canvas ref={chartRef} style={{ width: "100%", height: 400 }} />
    </div>
  );
}
