import React, { useEffect, useRef, useState } from "react";
import Chart from "chart.js/auto";

export default function LiveChart() {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    chartInstance.current = new Chart(ctx, {
      type: "line",
      data: {
        labels: [],
        datasets: [
          { label: "Price", data: [], borderColor: "lime", borderWidth: 2, tension: 0.4 },
          { label: "EMA100", data: [], borderColor: "orange", borderWidth: 1, tension: 0.4 },
          { label: "EMA300", data: [], borderColor: "red", borderWidth: 1, tension: 0.4 },
          { label: "Supertrend", data: [], borderColor: "blue", borderWidth: 1, borderDash: [5,5], tension: 0.4 }
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { labels: { color: "#fff" } },
        },
        scales: {
          x: { ticks: { color: "#ccc" } },
          y: { ticks: { color: "#ccc" } },
        },
      },
    });

    const socket = new WebSocket("ws://127.0.0.1:8000/ws/chart");

    socket.onopen = () => setConnected(true);

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      const time = new Date(data.timestamp).toLocaleTimeString();

      const chart = chartInstance.current;

      chart.data.labels.push(time);
      chart.data.datasets[0].data.push(data.price);
      chart.data.datasets[1].data.push(data.ema100);
      chart.data.datasets[2].data.push(data.ema300);
      chart.data.datasets[3].data.push(data.supertrend);

      // Add buy/sell markers
      if (data.signal === "buy") {
        chart.data.datasets.push({
          label: "BUY",
          data: [{ x: time, y: data.price }],
          borderColor: "green",
          backgroundColor: "green",
          pointRadius: 6,
          showLine: false,
        });
      } else if (data.signal === "sell") {
        chart.data.datasets.push({
          label: "SELL",
          data: [{ x: time, y: data.price }],
          borderColor: "red",
          backgroundColor: "red",
          pointRadius: 6,
          showLine: false,
        });
      }

      if (chart.data.labels.length > 30) {
        chart.data.labels.shift();
        chart.data.datasets.forEach(ds => ds.data.length > 0 && ds.data.shift());
      }

      chart.update();
    };

    socket.onerror = (err) => console.error("WebSocket error:", err);
    socket.onclose = () => setConnected(false);

    return () => socket.close();
  }, []);

  return (
    <div style={{ background: "#1f2833", padding: 20, borderRadius: 12, height: 500 }}>
      <h2 style={{ color: "#fff", textAlign: "center" }}>
        üìä Live Renko + EMA + Supertrend Chart
      </h2>
      <canvas ref={chartRef} style={{ width: "100%", height: "400px" }} />
      <p style={{ color: "#fff", textAlign: "center" }}>
        WebSocket: {connected ? "Connected ‚úÖ" : "Disconnected ‚ùå"}
      </p>
    </div>
  );
}
